INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ATTACK_AURA', '', 'Attack Aura', '', 'All figures you control within 4 clear sight spaces of <Unit> get +1 to attack. <Unit>?s Attack Aura does not effect <Unit>. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DEADLY_STRIKE', '', 'Deadly Strike', '', 'When attacking with <Unit>, all skulls rolled count for one additional hit.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MARRO_PLAGUE', '', 'Marro Plague', '', 'After moving and before attacking, you must roll the 20-sided die once for each figure adjacent to any <Unit>s you control. If you roll a 16 or higher, that figure receives a wound. Soulborgs and Wulsinu are not affected by Marro Plague.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('VYDAR_S_RANGE_ENHANCEMENT_AURA', '', 'Vydar''s Range Enhancement Aura', '', 'All friendly figures with a Range number of 4 or more who follow Vydar and are within 4 clear sight spaces of <Unit> add 2 to their Range number. Vydar?s Range Enhancement Aura does not affect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CHOMP', '', 'Chomp', '', 'Before attacking, choose one medium or small figure adjacent to <Unit>. If the chosen figure is a Squad figure, destroy it. If the chosen figure is a Hero figure, roll the 20-sided die. If you roll a 16 or higher, destroy the chosen hero. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HEAVY_SUPPORT_COMMAND_BEACON', '', 'Heavy Support Command Beacon', '', 'When revealing an order marker on <Unit>''s card, after taking his turn, if <Unit> is engaged, you may immediately take a turn with another Unique or Large Soulborg Hero or Squad you control that follows Utgar.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('IMPROVED_CYBERCLAW', '', 'Improved Cyberclaw', '', 'All small, medium or large opponent?s figures that enter or occupy a space adjacent to <Unit> may not move. Figures affected by the Cyberclaw cannot be moved by any special power on an Army Card or Glyph.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ENCIRCLE_SPECIAL_ATTACK', '', 'Encircle Special Attack', '', 'Range 1. Attack 6. If 3 <Unit>s you control are adjacent to the same enemy figure, they may roll their attack dice as one combined attack. The defending figure compares height to the lowest <Unit> to determine any height advantage. If Encircle Special Attack is used, the fourth figure that moved this turn cannot attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SMOKE_POWDER_13', '', 'Smoke Powder 13', '', 'When any <Unit> you control, or any figure you control that is adjacent to any <Unit> you control, is targeted for a normal attack from a non-adjacent opponent, you may roll the 20-sided die. If you roll a 13 or higher, all <Unit>s you control, and all figures you control that are adjacent to those <Unit>s, no longer have any visible hit zones for the duration of the targeting figure''s turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLAIL_HURRICANE', '', 'Flail Hurricane', '', 'After moving and before attacking with this <Unit>, you must roll the 20-sided die. If you roll a 1-10, it may attack normally. If you roll an 11 or higher, instead of attacking normally, it must attack each figure adjacent to it, if possible. Roll each attack separately.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HYDRA_HEADS', '', 'Hydra Heads', '', 'When this <Unit> attacks, it may attack up to 4 times. Reduce the number of times this <Unit> can attack by 1 for each wound marker on this Army Card. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ELVEN_SUPREMACY', '', 'Elven Supremacy', '', 'Any time you roll the 20-sided die for an Elf Army Card, you may add 1 to your die roll.', 0);
