INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DISCIPLINED_ARMY_DEFENSE_BONUS', '', 'Disciplined Army Defense Bonus', '', 'If every Army Card you control has a disciplined personality, each soldier in this Army Card receives 1 additional defense die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WATER_MASTERY', '', 'Water Mastery', '', 'While a <Unit> is on a water space, add 1 to its Attack and Defense.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHIELDS_OF_VALOR', '', 'Shields Of Valor', '', 'When defending with <Unit>s, each shield rolled counts for one additional block. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HEALING_TOUCH', '', 'Healing Touch', '', '"After moving and before attacking, choose a wounded Hero figure adjacent to <Unit>. Then roll the 20-sided die to add or remove wound markers from the chosen figure?s card:<br/>*If you roll 1, add 2 markers.<br/>*If you roll 2-5, remove 1 marker.<br/>*If you roll 6-17, remove up to 2 markers.<br/>*If you roll 18-20, remove all markers.<br/>"', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('QUEEN_OF_THIEVES', '', 'Queen of Thieves', '', 'After a tricky figure you control within 6 clear sight spaces of <Unit> rolls the 20-sided die for a special power on its Army Card, you may add 1 to your die roll. Queen of Thieves does not affect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CHALICE_OF_FORTITUDE', '', 'Chalice Of Fortitude', '', 'If <Unit> has 4 or more wounds on his Army Card, add 1 to his defense dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HEALING_WORD', '', 'Healing Word', '', 'After revealing an Order Marker on a wounded small or medium Hero figure that is adjacent to <Unit>, and before using any other special powers on any Army Card or Glyph, you may first roll the 20-sided die. If you roll a 1-12, nothing happens. If you roll a 13 or higher, remove up to 2 wound markers from that Hero?s Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PROTECTION_FROM_EVIL_AURA', '', 'Protection From Evil Aura', '', 'All small or medium figures you control within 3 clear sight spaces of <Unit> add 1 to their defense dice when rolling defense against an attack from a figure who follows Utgar. <Unit>?s Protection from Evil Aura does not effect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PSIONIC_EXPLOSION_SPECIAL_ATTACK', '', 'Psionic Explosion Special Attack', '', 'Range 4. Attack 3. Choose a figure to attack. Each opponent''s figure adjacent to the chosen figure is also affected by the the Psionic Explosion Special Attack. <Unit> does not need clear line of sight to attack with Psionic Explosion Special Attack. Roll 3 attack dice once for all affected figures. Each figure rolls defense dice separately.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WOLF_PACK', '', 'Wolf Pack', '', 'After revealing an Ordre Marker on the <Unit>'' card, before taking that turn with the Quickblades, you may choose one other Unique Wolf Army Card you control and take a turn with it. You may not attack with more figures from the chosen Army Card than the number of destroyed <Unit> on this card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TRIPLE_ATTACK', '', 'Triple Attack', '', 'When <Unit> attacks with a normal attack, he may attack two additional times.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CLIMB_X2', '', 'Climb X2', '', 'When moving up or down levels of terrain, <Unit>s may double their Height. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CLIMB_X3', '', 'Climb X3', '', 'When moving up or down levels of terrain, <Unit>s may triple their Height.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHIFTING_SANDS', '', 'Shifting Sands', '', 'After moving and before attacking, choose two small or medium figures on land spaces within 4 clear sight spaces of <Unit> and within 4 spaces of each other. Roll the 20-sided die. If you roll a 10 or higher, you may switch the two chosen figures. Figures moved by Shifting Sands never take leaving engagement attacks. <Unit> may be moved by Shifting Sands.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SOUL_DEVOUR', '', 'Soul Devour', '', 'Before moving, each <Unit> you control may choose an adjacent unique hero. Roll the 20-sided die once for each <Unit>. If you roll 19 or 20, destroy the <Unit> figure, then take control of the chosen unique hero and remove any Order Marker on its card. You now control that Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MIND_LINK', '', 'Mind Link', '', 'Instead of taking a turn with <Unit>, you may take a turn with up to 2 different Elf Wizards you control. <Unit> cannot be one of the 2 Elf Wizards. Any Elf Wizard that is taking a turn instead of <Unit> must be within 6 clear sight spaces of <Unit> before moving.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SWING_LINE_4', '', 'Swing Line 4', '', 'Instead of a normal move, <Unit> may use his Swing Line. Swing Line has a move of 4. When counting spaces for <Unit>''s Swing Line movement, ignore elevations. <Unit> may swing over water without stopping, swing over figures without becoming engaged, and swing over obstacles such as ruins. <Unit> may not Swing Line more than 40 levels up or down in a single Swing Line. If <Unit> is engaged when he starts to Swing Line, he will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DISHONORABLE_ATTACK', '', 'Dishonorable Attack', '', 'When attacking a figure who follows Jandar, <Unit> rolls 2 additional attack dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DEVOUT_LEADERSHIP', '', 'Devout Leadership', '', 'All figures you control with a devout personality move one additional space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LANDSLIDE', '', 'Landslide', '', 'A <Unit> with a height advantage on an adjacent opponent?s figure rolls an additional attack die when attacking that figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLOODLUST', '', 'Bloodlust', '', 'For every opponent?s figure a <Unit> destroys, place a purple Experience Marker on this card. When attacking with <Unit>, roll one additional attack die for each Experience Marker on this card. A maximum of 3 Experience Markers can be placed on this card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ETERNAL_HEARTBREAK', '', 'Eternal Heartbreak', '', 'If you control Cyprien Esenwein and he is destroyed, <Unit> immediately receives 2 wounds.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HUNTER_S_AIM_SPIRIT', '', 'Hunter''s Aim Spirit', '', 'When <Unit> is destroyed, place this figure on any Unique Army Card with a Range number of 4 or more. <Unit>''s Spirit adds 2 to the Range number of that card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GUNNER_SPECIAL_ATTACK', '', 'Gunner Special Attack', '', '"Range 5. Attack 3.<br/>After attacking normally, <Unit> may attack with Gunner Special attack once for each Dwarf figure on this card. While engaged, <Unit> may target and attack non-adjacent figures with Gunner Special Attack. <Unit> cannot attack the same figure twice with Gunner Special Attack."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GIANT_KILLER', '', 'Giant Killer', '', 'When <Unit> attacks Huge figures, add 2 attack dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLOODBORN_RISING', '', 'Bloodborn Rising', '', 'Each time <Unit> destroys a figure, you may remove a wound marker from this Army Card. If the destroyed figure was small or medium, and not undead, replace that figure immediately, if possible, with one of your own previously destroyed Thralls. <Unit> may not use Bloodborn Rising on destructible objects.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('REGENERATE', '', 'Regenerate', '', 'After taking a turn with this <Unit>, remove 1 wound marker from this <Unit>?s Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DEFENSIVE_VAULT', '', 'Defensive Vault', '', 'After a <Unit> rolls defense dice against a normal attack from an adjacent opponent''s figure, if she is still adjacent to the attacking figure, you may place that <Unit> on any other space adjacent to the attacking figure, if possible. A <Unit> does not take any leaving engagement attacks when using Defensive Vault.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MORTAL_STRIKE', '', 'Mortal Strike', '', 'After attacking an opponent?s Unique Hero with this <Unit>''s normal attack, roll 1 attack die for each wound inflicted in that attack. For each additional skull rolled, place 1 additional wound marker on the defending figure?s Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('UTGAR_SOULBORG_BONDING', '', 'Utgar Soulborg Bonding', '', 'Before taking a turn with <Unit>, you may take a turn with any Soulborg Hero you control who follows Utgar.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DRAGON_HEALING', '', 'Dragon Healing', '', 'Before moving, you may choose a wounded Hero figure within 4 clear sight spaces of <Unit>. Roll the 20-sided die. If you roll a 15 or higher, remove one wound marker from the chosen Hero''s Army Card. <Unit>''s Dragon can use its Dragon Healing on <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHADOW_DANCE', '', 'Shadow Dance', '', 'If <Unit> starts her turn on a shadow space, instead of moving normally, you may place her on any other empty shadow space within 8 spaces of her current location. If <Unit> is engaged when she starts her Shadow Dance, she will take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EXPLOIT_WEAKNESS', '', 'Exploit Weakness', '', 'At any point before, during, or after movement, you may choose one opponent''s figure within 6 clear sight spaces of <Unit>. All of the special powers on the chosen figure''s card (or cards if your opponent has more than one Common Army Card for that figure) are negated until the end of <Unit>''s turn. <Unit> can only use Exploit Weakness once per turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DWARVEN_STRATEGIC_BONDING', '', 'Dwarven Strategic Bonding', '', 'Before taking a turn with <Unit>, you may first take a turn with any Dwarf Hero you control. If you do not take a turn with any Dwarf Hero you control, add 2 to the <Unit>? move number.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('UNLEASHED_FURY', '', 'Unleashed Fury', '', 'Before moving, roll the 20-sided die. If you roll a 1, choose an <Unit> you control and destroy it. If you roll a 2-6, add 1 to the attack value of this card. If you roll a 7-11, add 2 to the attack value of this card. If you roll 12-15, add 3 to the attack value of this card. If you roll a 16-19, add 4 to the attack value of this card. If you roll a 20, add 8 to the attack value of this card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HEAT_BLAST_SPECIAL_ATTACK', '', 'Heat Blast Special Attack', '', '"Range 2. Attack Special.<br/>Heat Blast Special Attack affects all figures within 2 clear sight spaces of <Unit>. Roll one attack die for each Growing Heat Marker on this Army Card. Roll attack dice once for all affected figures. Each figure rolls defense dice seperately. Heat Blast Special Attack does not affect <Unit> or figures with the Lava Resistant special power. After attacking with Heat Blast Special Attack, remove all Growing Heat Markers from this Army card."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FROST_BREATH_SPECIAL_ATTACK', '', 'Frost Breath Special Attack', '', '"Range 6. Attack 4.<br/>Figures with the Lava Resistant special power roll 2 less defense dice when attacked by <Unit>''s Frost Breath Special Attack."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('QUEGLIX_GUN_SPECIAL_ATTACK', '', 'Queglix Gun Special Attack', '', 'Range 6. Attack 1, 2 or 3.  <Unit> starts each turn with 9 attack dice. Choose any figure within range and attack by rolling 1, 2 or 3 attack dice. <Unit> may keep making special attacks with 1, 2 or 3 attack dice until he has rolled all 9 attack dice. <Unit> may target the same or different figures with each attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ULLAR_WARLORD_BONDING', '', 'Ullar Warlord Bonding', '', 'Before taking a turn with this creature, you may first take a turn with any Warlord you control that follows Ullar.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('AIR_MASTERY', '', 'Air Mastery', '', 'Figures that have the Flying or Stealth Flying special power subtract 1 from their defense dice when attacked by a <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SNEAK_ATTACK_2', '', 'Sneak Attack 2', '', 'If <Unit> is attacking an opponent?s figure that is adjacent to at least one other figure you control, add 2 to <Unit>?s attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SPIDEY_SENSE_14', '', 'Spidey Sense 14', '', 'If <Unit> is attacked by an opponent''s figure and at least 1 skull is rolled, roll the 20-sided die. If you roll 1-10, roll defense dice normally, If you roll an 11-20, Spider-Man takes no damage and may immediately move using his Swing Line 4 Special Power.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SPIDEY_SENSE_11', '', 'Spidey Sense 11', '', 'If <Unit> is attacked by an opponent''s figure and at least 1 skull is rolled, roll the 20-sided die. If you roll 1-13, roll defense dice normally, If you roll a 14-20, Venom takes no damage and instead may immediately move using his Swing Line 4 Special Power.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ROD_OF_NEGATION', '', 'Rod Of Negation', '', 'Start the game with 3 brown Negation Markers on this card. At the end of the turn, if you have at least 1 Negation Marker on this card, you may choose any opponent?s unique figure within 6 clear sight spaces of <Unit>. Roll the 20-sided die. If you roll a 1-15, nothing happens. If you roll a 16-19, place a Negation Marker on the chosen figure?s Army Card. All of that figure?s special powers are negated for the entire game. If you roll a 20, destroy the chosen figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHADOW_AMBUSH', '', 'Shadow Ambush', '', 'If <Unit> starts her turn unengaged and on a shadow space, add 3 dice to her attack this turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EMP_RESPONSE', '', 'EMP Response', '', 'If a <Unit> you control successfully defends against an attack by a Soulborg figure, you must roll the 20-sided die. If you roll a 14 or higher, the attacking figure must immediately end its turn and all order markers must be removed from its Army Card (or cards if your opponent has more than one Common Army Card for that figure).', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('KYRIE_LEADERSHIP', '', 'Kyrie Leadership', '', 'All Kyrie you control, except <Unit>, move one additional space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SLIPPERY_6', '', 'Slippery 6', '', 'Once per turn, if an opponent?s squad figure ends its movement adjacent to <Unit>, you may roll the 20-sided die. If you roll a 6 or higher, <Unit> may immediately move up to 4 spaces. <Unit> will not take any leaving engagement attacks when moving with Slippery. <Unit> can use this power only if she ends her Slippery move not adjacent to any enemy figures.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FANATICAL_ADVANCE', '', 'Fanatical Advance', '', '<Unit> is never attacked when leaving an engagement with a Squad figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TASKMASTER_BONDING', '', 'Taskmaster Bonding', '', 'Before taking a turn with the <Unit>, you may first take a turn with any large wild Hero you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLEDGLING_LIGHTNING_BREATH_SPECIAL_ATTACK', '', 'Fledgling Lightning Breath Special Attack', '', 'Range 4 + Special. Attack 2. Choose a figure to attack. You may also choose one other figure within 3 clear sight spaces of the targeted figure to be affected by Fledgling Lightning Breath Special Attack. Roll attack dice once for both figures. Each figure rolls defense dice separately. Fledgling Lightning Breath Special Attack does not affect destructible objects.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MAUL', '', 'Maul', '', 'When rolling attack dice against a small or medium figure, if a <Unit> rolls a skull on every die, the defending figure receives a wound for every skull, and cannot roll any defense dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CLOUD_OF_DARKNESS', '', 'Cloud Of Darkness', '', 'After taking a turn with <Unit>, you may roll the 20-sided die. If you roll a 16 or higher, while she remains on that space, <Unit> has no visible Hit Zone until the next time you reveal an order marker.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MARRO_REBIRTH', '', 'Marro Rebirth', '', 'After taking a turn with <Unit>, you may roll the 20-sided die. If you roll a 13 or higher, you may place any previously destroyed common Marro Squad figure from your army on an empty space adjacent to <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STINGER_DRAIN', '', 'Stinger Drain', '', 'After moving and before attacking, you may roll the 20-sided die. If you roll a 1-4, you must destroy a <Unit> you control and you cannot attack this turn. If you roll a 5-9, add 0 to the attack value of this card. If you roll a 10 or higher, add 1 to the attack value of this card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PARALYZING_FEAR', '', 'Paralyzing Fear', '', 'Figures attacked by any Undead subtract one from their defense dice for every <Unit> they are engaged to.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STAB_IN_THE_BACK', '', 'Stab In The Back', '', 'After you take a turn with <Unit>, you must roll the 20-sided die. If you roll a 1, choose an opponent. That opponent now controls <Unit>. Remove any order markers on this Army Card, then give the card to that opponent.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WILD_PACK_MOVEMENT', '', 'Wild Pack Movement', '', 'Before moving, roll the 20-sided die. If you roll a 1-3, add 1 to the move value of this card. If you roll a 4-6, add 3 to the move value of this card. If you roll a 7-20, add 7 to the move value of this card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GLADIATOR_INSPIRATION', '', 'Gladiator Inspiration', '', 'If all Order Markers for a round are placed on Gladiator Army Cards, and at least one Order Marker is placed on <Unit>, then all Gladiators you control (except <Unit>) become inspired. Inspired Gladiators add one to their Move number and add 1 extra attack and defense die for the rest of the round.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HUMAN_CHAMPION_BONDING', '', 'Human Champion Bonding', '', 'Before taking a turn with these creatures, you may first take a turn with any Human Champion you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GALLOPING_CHARGE', '', 'Galloping Charge', '', 'A <Unit> receives 2 additional attack dice when attacking any figure that was at least 4 clear sight spaces away from that <Unit> at the start of his turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MINDLESS_PACK', '', 'Mindless Pack', '', '"After revealing an order marker on this card, roll the 20-sided die.<br/>*If you roll a 1-5, you may take a turn with 1 Thrall you control.<br/>*If you roll a 6-15, you may take a turn with up to 2 Thralls you control.<br/>*If you roll a 16 or higher, you may take a turn with up to 3 Thralls you control."', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('QUICK_STAB_SPECIAL_ATTACK', '', 'Quick Stab Special Attack', '', '"Range 1. Attack 2.<br/>Small and medium figures may not roll defense dice when attacked by Quick Stab Special Attack."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SLITHER', '', 'Slither', '', '<Unit> does not have to stop his movement when entering water spaces.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MASTER_S_INFLUENCE', '', 'Master''s Influence', '', 'All Monk Squad figures you control may leap an additional 13 levels up or down when using their Stealth Leap special power. All Monks you control within 2 clear sight spaces of <Unit> add 1 to their attack dice and 1 to their defense dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('REDUNDANT_SYSTEMS', '', 'Redundant Systems', '', 'Start the game with 3 Redundant Systems markers on this army card. After a <Unit> receives one or more wounds, you may remove 1 Redundant Systems marker from this card to ignore all wounds.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HIDE_IN_DARKNESS', '', 'Hide In Darkness', '', 'If <Unit> is attacked with a normal attack and at least 1 skull is rolled, roll the 20-sided die. If <Unit> is on a dungeon space, add 3 to your die roll. If he is on a shadow space, add 6 to your die roll. If you roll a 1-15, roll defense dice normally. If you roll a 16 or higher, ignore all damage that would be inflicted by the attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DISCIPLINED_INFLUENCE', '', 'Disciplined Influence', '', 'At the start of the game, you may choose any unique Army Card you control. For this game the chosen card?s personality is disciplined, regardless of what is listed on the card. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('RAIL_GUN_SPECIAL_ATTACK', '', 'Rail Gun Special Attack', '', 'Range 7. Attack 4. When <Unit> attacks an opponent''s figure that is not adjacent with his Rail Gun Special Attack, you must choose a figure adjacent to the targeted figure, if possible. If <Unit> inflicts more wounds than needed to destroy the targeted figure, all excess wounds count as unblockable hits on the chosen figure.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('+2_BROADSWORD', '', '+2 Broadsword', '', 'If <Unit> is attacking an adjacent figure, add 2 dice to <Unit>?s attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GLIDE', '', 'Glide', '', '<Unit> never rolls for falling damage.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLIND_RAGE_SPECIAL_ATTACK', '', 'Blind Rage Special Attack', '', 'Range 1. Attack 3.  If <Unit> rolls at least 2 skulls with his Blind Rage Special Attack, <Unit> may attack again with his Blind Rage Special Attack. <Unit> may continue attacking with his Blind Rage Special Attack until he rolls fewer than 2 skulls.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SCALE', '', 'Scale', '', 'When moving up or down levels of terrain, <Unit>s may add 2 to their height.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('VAULT', '', 'Vault', '', 'After moving and before attacking, any or all unengaged <Unit>s may Vault. Choose an opponent''s figure within 2 clear sight spaces and place that <Unit> on any unoccupied space adjacent to the chosen figure. A <Unit> may not vault more than 7 levels up or down in a single vault.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DEADLY_SHOT', '', 'Deadly Shot', '', 'When attacking with <Unit>, each skull rolled counts as an additional hit.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('COMMAND_DISPATCH', '', 'Command Dispatch', '', 'After taking a turn with a <Unit>, if that <Unit> did not attack, you may choose an adjacent small or medium Unique Hero you control that follows Vydar and take a turn with that Hero.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ULLAR_S_BOLT_OF_THE_WITHERWOOD', '', 'Ullar''s Bolt Of The Witherwood', '', 'After moving and before attacking with <Unit>, you may choose any opponent?s figure within 5 clear sight spaces of <Unit>. Roll the 20-sided die. If you roll a 1-15, nothing happens. If you roll a 16 or higher, the chosen figure is destroyed. <Unit> may attempt to use this power only once per game.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLOOD_FRENZY', '', 'Blood Frenzy', '', 'When attacking with this <Unit>, it receives 1 extra attack die for each wound marker on the defending figure?s Army Card, to a maximum of 3 additional attack dice for Blood Frenzy.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('UNHOLY_BONDING', '', 'Unholy Bonding', '', 'Before taking a turn with the <Unit>, you may first take a turn with any small, medium, or large relentless Hero you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GROK_TRAINING', '', 'Grok Training', '', 'Marro Hive cannot rebirth <Unit> with its Marro Rebirth special power.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SUMMON_ELEMENTAL', '', 'Summon Elemental', '', 'After moving and before attacking with <Unit>, you may roll the 20-sided die. If you roll an 11 or higher, you may, if possible, place one of your previously destroyed small or medium Elementals on an empty space within 3 clear sight spaces of <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CARRY', '', 'Carry', '', 'Before moving <Unit>, choose an unengaged friendly small or medium figure adjacent to <Unit>. After you move <Unit>, place the chosen figure adjacent to <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TO_THE_LAST_MAN', '', 'To The Last Man', '', 'Add 1 to the <Unit>''s Attack and Defense values for each previously destroyed <Unit> fighter on this Army Card. A <Unit> fighter may not attack other friendly <Unit> fighters.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('INITIATIVE_ADVANTAGE', '', 'Initiative Advantage', '', 'If all of your order markers are on Gladiator Army Cards, you may add 1 to your initiative roll for every order marker on the <Unit>?s Army Card, up to a maximum of +3 for Initiative Advantage.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SIGHTING', '', 'Sighting', '', 'When a <Unit> is attacking with a height advantage, he rolls an additional attack die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('AEGIS_OF_THE_CRIMSON_SIGIL', '', 'Aegis Of The Crimson Sigil', '', 'When <Unit>, or any Elf Wizard you control adjacent to <Unit>, rolls defense dice, add one automatic shield to whatever is rolled for each revealed order marker on <Unit>''s Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LEAF_OF_THE_HOME_TREE_AURA', '', 'Leaf of the Home Tree Aura', '', 'When any Elf you control within 8 clear sight spaces of <Unit> receives enough wounds to be destroyed, you may roll 10 Ullar Valkyrie dice before removing the figure. If you roll at least 3 Ullar symbols, ignore any wounds that figure just received. <Unit>?s Leaf of the Home Tree Aura does not effect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('THUNDER_RAM_ASSAULT_SPECIAL_ATTACK', '', 'Thunder Ram Assault Special Attack', '', 'Range 1 + Special. Attack 4. Choose a figure to attack. You may also choose up to 2 other figures within 3 clear sight spaces of the targeted figure to be affected by Thunder Ram Assault Special Attack. Roll attack dice once for all affected figures. Each figure rolls defense dice separately. You can only use Thunder Ram Assault Special Attack if <Unit> was not engaged with the targeted figure at the start of this turn. You cannot use Thunder Ram Assault Special Attack if you used Thunder Step this turn.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EARTH_SLAM_SPECIAL_ATTACK', '', 'Earth Slam Special Attack', '', 'Range 1. Attack 3.  Any non-flying figure adjacent to this <Unit> is affected by Earth Slam Special Attack. Roll attack dice once for all affected figures. Each figure rolls defense dice separately. ', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CLEAVE', '', 'Cleave', '', 'When <Unit> attacks an adjacent figure, you may choose one Squad figure that is also adjacent to <Unit>. If the defending figure receives any wounds from <Unit>?s attack, the chosen figure receives 1 wound.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('THORIAN_SPEED', '', 'Thorian Speed', '', 'Opponent''s figures must be adjacent to <Unit> to attack it with a normal attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TACTICAL_DISENGAGEMENT_7', '', 'Tactical Disengagement 7', '', 'When <Unit> receives one or more wounds from a leaving engagement attack, immediately roll the 20-sided die. If you roll a 7 or higher, ignore any wounds from that leaving engagement attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PRIORITY_TARGETING', '', 'Priority Targeting', '', 'An engaged <Unit> may target and attack non-adjacent figures unless that <Unit> is engaged with a hero figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SWAMP_WATER_STRENGTH', '', 'Swamp Water Strength', '', 'When a <Unit> is on a swamp water space, add 1 to its attack and defense.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WATER_CLONE', '', 'Water Clone', '', 'Instead of attacking with all of the <Unit>s, one at a time, roll the 20-sided die for each <Unit> in play. If you roll a 15 or higher, place a previously destroyed <Unit> on a same-level space adjacent to that <Unit>. Any <Unit> on a water space needs a 10 or higher to Water Clone. You may only Water Clone after you move.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WAIT_THEN_FIRE', '', 'Wait Then Fire', '', 'If none of the <Unit> move this turn, add 1 die to their attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ATTACK_AURA_1', '', 'Attack Aura 1', '', 'All friendly figures adjacent to <Unit> with a range of 1 add 1 die to their normal attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORC_CHAMPION_BONDING', '', 'Orc Champion Bonding', '', 'Before taking a turn with this creature, you may first take a turn with any Orc Champion you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLEET_FOOTED', '', 'Fleet Footed', '', 'If a <Unit> is attacked with a normal attack by an opponent?s figure and at least 1 skull is rolled, roll the 20-sided die. If you roll 1-10, roll defense dice normally. If you roll an 11 or higher, that <Unit> takes no damage and instead may move up to 4 spaces. A figure moving with Fleet Footed never takes any leaving engagement attacks. A <Unit> can use Fleet Footed only if he ends his move not adjacent to any enemy figures.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLOOD_HUNGER', '', 'Blood Hunger', '', 'Before moving a <Unit>, you may choose an opponent''s figure. Add 1 to the <Unit>''s Move and Attack values for each wound marker on the chosen figure''s Army Card to a maximum of +3 for each. To use Blood Hunger, the <Unit> must end its move adjacent to the chosen figure and must attack that figure if possible. If the <Unit> does not inflict at least one wound on the chosen figure, destroy the <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('NECROMANCY', '', 'Necromancy', '', 'When a <Unit> you control is destroyed, roll the 20-sided die. If you roll an 11 or higher, place that <Unit> on the Army Card of any Unique Lord, Lady, Duke or Duchess you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TWO_HEADS_ARE_BETTER_THAN_ONE', '', 'Two Heads Are Better Than One', '', 'After attacking with <Unit>, if there are 3 or fewer wound markers on this Army Card, they may attack one additional time.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GUERRILLA_TACTICS', '', 'Guerrilla Tactics', '', 'After taking a turn with the <Unit>, for each <Unit> you control that attacked an opponent''s figure this turn, you may move any Mariedian figure you control that did not attack this turn up to 3 spaces. A Mariedian figure may only be moved by Guerrilla Tactics once per turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('RIGHTEOUS_SMITE', '', 'Righteous Smite', '', 'When attacking an opponent?s figure who follows Utgar, <Unit>s receive 1 additional attack die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('NATURE_S_PROTECTION', '', 'Nature''s Protection', '', 'When an <Unit> rolls defense dice while adjacent to a tree or bush, one shield will block all damage.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('POISON_STING_SPECIAL_ATTACK', '', 'Poison Sting Special Attack', '', 'Range 1. Attack 4.  If <Unit> inflicts at least 1 wound with Poison Sting Special Attack, roll the 20-sided die for Poison Damage. If you roll 1-9, the defending figure receives no additional wounds for Poison Damage. If you roll 10-19, add 1 additional wound marker to the defending figure''s Army Card, and roll again for Poison Damage. If you roll a 20, destroy the defending figure.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('UTGAR_S_ORDERS', '', 'Utgar''s Orders', '', 'Instead of taking a turn with the <Unit>, you may take a turn with any Kyrie Warrior who follows Utgar. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('THUNDER_STEP', '', 'Thunder Step', '', 'Instead of moving <Unit> normally, you may choose an opponent?s figure within 5 clear sight spaces of <Unit>. Place <Unit> on any empty space adjacent to the chosen figure. For the duration of <Unit>?s turn, add 1 to her attack value. At the end of her turn, roll one attack die. If a skull is rolled, place one wound marker on <Unit>?s Army Card. If <Unit> is engaged when she starts Thunder Step, she will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DOUBLE_ATTACK', '', 'Double Attack', '', 'When <Unit> attacks, he may attack one additional time.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('JANDAR_S_DISPATCH', '', 'Jandar''s Dispatch', '', 'After you take a turn with <Unit>, roll 12 Jandar Valkyrie dice. You may move up to 4 squad figures you control who follow Jandar up to X spaces. X equals the number of Jandar symbols rolled. Any squad figures moved with Jandar?s Dispatch must be within 8 clear sight spaces of <Unit> prior to moving.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ETERNAL_STRENGTH', '', 'Eternal Strength', '', 'Anytime you roll the 20-sided die for Cyprien Esenwein?s Chilling Touch, you may add 2 to your die roll.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORB_OF_PAIN_SPECIAL_ATTACK', '', 'Orb of Pain Special Attack', '', 'Range 4.  Attack 1, 2, or 3.  Quorik Warwtich starts each turn with 6 attack dice.  Choose any non-adjacent figure within range and attack by rolling 1, 2, or 3 attack dice.  Quorik Warwtich may keep making special attacks with 1, 2, or 3 attack dice untill she has rolled all 6 attack dice.  Quorik Warwtich may target the same or different figures with each attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TETSUBO_SPECIAL_ATTACK', '', 'Tetsubo Special Attack', '', 'Range 1. Attack 3. Choose a figure to attack. You may also choose one figure adjacent to the targeted figure to be affected by Tetsubo Special Attack. Roll attack dice once for both figures. Each figure rolls defense dice separately.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WATER_TUNNEL', '', 'Water Tunnel', '', 'If a <Unit> ends its normal movement on a water space, you may immediately place it on any empty same-level water space within 5 spaces. If a <Unit> is engaged when it starts its Water Tunnel, it will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DISHONORABLE_LEADERSHIP', '', 'Dishonorable Leadership', '', 'When revealing an Order Marker on <Unit>, after taking <Unit>''s turn, if <Unit> is unengaged, you may take a turn with one of the following that you control: ? 1 Ninja Squad or ? up to 2 Ninja Heroes. Any figure in the above list that is taking a turn with Dishonorable Leadership must be within clear sight of <Unit> before moving.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HYPER_SPEED_5', '', 'Hyper Speed 5', '', 'After taking a turn with <Unit>, <Unit> may move up to an additional 5 spaces. <Unit> cannot use Hyper Speed 5 if he attacked with his Cosmic Force Blast Special Attack during that turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('NET_TRIP_14', '', 'Net Trip 14', '', 'After moving and before attacking, roll the 20-sided die. If you roll a 14 or higher, any small or medium figure attacked by <Unit> this turn may roll no more than 1 die for defense.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LIZARD_KING_BONDING', '', 'Lizard King Bonding', '', 'Before taking a turn with the <Unit>, you may first take a turn with their chosen Lizard King if it is still under your control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHOTGUN_BLAST_SPECIAL_ATTACK', '', 'Shotgun Blast Special Attack', '', 'Range 5. Attack 3.  Choose a figure to attack. Any figures adjacent to the chosen figure are also affected by the Shotgun Blast Special Attack. <Unit> only needs a clear sight shot at the chosen figure. Roll attack dice once for all affected figures. Each figure rolls defense dice separately. <Unit> cannot be affected by his own Shotgun Blast Special Attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DEFENSIVE_AURA', '', 'Defensive Aura', '', 'All figures you control within 4 clear sight spaces of <Unit> add 2 to their defense dice. <Unit>?s Defensive Aura does not affect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SCOUT_MELEE_ATTACK_ENHANCEMENT', '', 'Scout Melee Attack Enhancement', '', 'All friendly Scouts adjacent to <Unit> receive an additional attack die when attacking a figure adjacent to them.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GORILLINATOR_ATTACK_BONDING', '', 'Gorillinator Attack Bonding', '', 'After revealing an order marker on <Unit>, you may take a turn with a squad of Gorillinators you control before taking a turn with <Unit>. The Gorillinators cannot move during this turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FROST_RAGE', '', 'Frost Rage', '', 'When revealing a numbered order marker on <Unit>?s Army Card, you may also reveal an ?X? order marker that is on <Unit>?s Army Card to activate Frost Rage for the duration of the round. While Frost Rage is active, add 1 to <Unit>?s Attack and Defense values for each wound marker on his Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ROAD_STRENGTH', '', 'Road Strength', '', 'Add 1 to <Unit>?s attack and defense while on a road space. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ICE_SPIKES_15', '', 'Ice Spikes 15', '', 'If an opponent''s figure moves adjacent to this <Unit>, roll the 20-sided die. If this <Unit> is on at least one ice or snow space, add 2 to your die roll. If you roll a 15 or higher, the opponent''s figure receives 1 wound. Figures can be affected by Ice Spikes only as they move into engagement with this <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('KNOCKBACK_14', '', 'Knockback 14', '', 'When a small or medium figure rolls defense dice and is not destroyed by a normal or special attack from <Unit>, immediately roll the 20-sided die. If you roll a 14 or higher, you may Knockback the figure by placing it on any empty space within 3 clear sight spaces of <Unit>. The space must be on the same level as or lower than its current location. A figure moved by Knockback never takes any leaving engagement attacks. A non-flying figure moved lower by Knockback can receive any falling damage that may apply.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LOLTH_S_WRATH_SPECIAL_ATTACK', '', 'Lolth''s Wrath Special Attack', '', 'Range Special. Attack 3.  Choose a Drow figure you control within 5 clear sight spaces of <Unit> and roll 3 attack dice. One at a time for each skull rolled, you may inflict 1 wound on any small or medium figure that is within 2 clear sight spaces of the chosen Drow figure. You may inflict more than 1 wound on a single figure using Lolth?s Wrath Special Attack. Figures affected by Lolth?s Wrath Special Attack cannot roll any defense dice. After attacking with Lolth?s Wrath Special Attack, destroy the chosen Drow figure.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SPLIT_DECISION', '', 'Split Decision', '', 'At the start of <Unit>''s turn, if there are 3 or fewer wound markers on this Army Card, you must roll the 20-sided die. If you roll a 6 or lower, <Unit> cannot attack if they move this turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GRAPPLE_ARM', '', 'Grapple Arm', '', 'Instead of his normal move, <Unit> may use his Grapple Arm. Grapple Arm has a move of 4. When counting spaces for Grapple Arm, ignore elevations. <Unit> may grapple over water without stopping, over figures without becoming engaged, and over obstacles such as ruins. <Unit> may not grapple more than 45 levels up or down in a single Grapple Arm move. If <Unit> is engaged when he starts his Grapple Arm move, he will take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HIVE_SWARM', '', 'Hive Swarm', '', 'Before moving <Unit>s, roll the 20-sided die. If you roll 1-12, you may move and attack with up to 3 <Unit>s you control. If you roll a 13-16, you may move and attack with up to 6 <Unit>s you control. If you roll a 17-20, you may move and attack with up to 9 <Unit>s you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BARGE_INTO_BATTLE', '', 'Barge Into Battle', '', 'After moving and before attacking, if a <Unit> you control ends its movement unengaged, you may choose an engaged small or medium friendly Squad figure within 3 clear sight spaces of that <Unit> whose base is no more than 2 levels higher or lower than the base of that <Unit>. You may switch that <Unit> with the chosen Squad figure. Figures moved by Barge into Battle never take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SELF-REPLICATING', '', 'Self-Replicating', '', 'Marro Hive cannot rebirth Marro Dividers with its Marro Rebirth special power.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ROCK_THROW', '', 'Rock Throw', '', 'If a <Unit> has a height advantage on an opponent?s figure, it may add 2 to its range when attacking that figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SHURIKEN_SPECIAL_ATTACK', '', 'Shuriken Special Attack', '', 'Range 5. Attack Special. If this unit is attacking a small or medium figure, roll 3 attack dice for Shuriken Special Attack. If this unit is attacking a figure of a different size, roll 2 attack dice for Shuriken Special Attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MELEE_DEFENSE_#', '', 'Melee Defense #', '', 'When rolling defense dice against a normal attack from an adjacent figure, <Unit> adds # dice.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GUARD_LEADERSHIP', '', 'Guard Leadership', '', 'All Guards you control move one additional space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('POISON_CLOUD', '', 'Poison Cloud', '', 'When a <Unit> attacks a Common figure, add 1 to its Attack value.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EXPLOSIVE_ROUNDS_SPECIAL_ATTACK', '', 'Explosive Rounds Special Attack', '', '"Range 5. Attack 3.<br/>Choose a figure to attack. You may also choose one other figure adjacent to the targeted figure to be affected by Explosive Rounds Special Attack. Roll attack dice once for both figures. Each figure rolls defense dice separately. When <Unit> attacks with his Explosive Rounds Special Attack, he may attack one additional time."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORC_DEFENSIVE_AURA_1', '', 'Orc Defensive Aura 1', '', 'All Orc creatures you control within 4 clear spaces of <Unit> add 1 to their defense dice.  This creature''s Defensive Aura does not affect itself.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PLASMA_SURGE', '', 'Plasma Surge', '', 'After attacking a non-adjacent figure with a <Unit> you control, you must roll the 20-sided die. If you roll a 1-6, destroy that <Unit>. If you roll a 7-17, that <Unit> is safe. If you roll an 18-20, you may attack again with that <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('GLACIER_TRAVERSE', '', 'Glacier Traverse', '', 'If a <Unit> is adjacent to a Glacier Mountain, the <Unit> may Glacier Traverse instead of moving normally. You may do this with any or all <Unit>s you control each turn. To Glacier Traverse, move to any unoccupied space adjacent to that Glacier Mountain.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('KNIGHT_AND_SENTINEL_ENHANCEMENT', '', 'Knight And Sentinel Enhancement', '', 'All friendly Knights and Sentinels adjacent to <Unit> roll an additional attack die and an additional defense die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ZOMBIE_RISES_AGAIN', '', 'Zombie Rises Again', '', 'If an Undead Savage that you control destroys an opponent''s large or huge figure, replace that figure immediately, if possible, with one of your own previously destroyed <Unit>s, and remove all markers other than order markers on that card. Newly placed <Unit>s cannot attack this turn. Undead are not affected by Zombie Rises Again.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HIVE_MIND', '', 'Hive Mind', '', 'After revealing an Order Marker on this Army Card you may take a turn with any small or medium common Marro Squad you control before taking a turn with <Unit>. Any figure that is taking a turn must be within 12 clear sight spaces of <Unit> prior to its movement.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SILVER_BOLTS_SPECIAL_ATTACK', '', 'Silver Bolts Special Attack', '', 'Range 5. Attack 3. When a Lycanthrope or Hybrid figure receives one or more wounds from Silver Bolts Special Attack, that figure receives one additional wound.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WARRIOR_S_ARMOR_SPIRIT_1', '', 'Warrior''s Armor Spirit 1', '', 'When <Unit> is destroyed, place this figure on any unique Army Card. <Unit>?s Spirit add 1 to the defense number on that card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WHIP_12', '', 'Whip 12', '', 'After moving and before attacking, choose a small or medium figure adjacent to <Unit>. Roll the 20-sided die. If you roll a 12 or higher, the chosen figure cannot roll any defense dice if attacked by <Unit> this turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EXPLOSION_SPECIAL_ATTACK', '', 'Explosion Special Attack', '', 'Range 7. Attack 3.  Choose a figure to attack. Any figures adjacent to the chosen figure are also affected by the Explosion Special Attack. <Unit> only needs a clear sight shot at the chosen figure. Roll 3 attack dice once for all affected figures. Each figure rolls defense dice separately. <Unit> can be affected by his own Explosion Special Attack. ', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('THE_DROP', '', 'The Drop', '', '<Unit>s do not start the game on the battlefield. At the start of each round, before you place Order Markers, roll the 20-sided die. If you roll a 13 or higher you may place all # <Unit> figures on any empty spaces. You cannot place them adjacent to each other or other figures, or on glyphs.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ZOMBIES_RISE_AGAIN', '', 'Zombies Rise Again', '', 'If a <Unit> that you control destroys an opponent''s small or medium figure, replace that figure immediately, if possible, with one of your own previously destroyed <Unit>s. Newly placed <Unit>s cannot attack this turn. Undead are not affected by Zombies Rise Again.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EVISCERAXE_SPECIAL_ATTACK', '', 'Evisceraxe Special Attack', '', 'Range 1. Attack 5.  The Evisceraxe Special Attack cannot be used on small figures.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('THROW_14', '', 'Throw 14', '', 'After moving and before attacking, choose one small or medium non-flying figure adjacent to <Unit>. Roll the 20-sided die. If you roll a 14 or higher, you may throw the figure by placing it on any empty space within 4 spaces of <Unit>. The figure must land within clear sight of <Unit>. After the figure is placed, roll the 20-sided die for throwing damage. If you roll an 11 or higher, the thrown figure receives 2 wounds. If the figure is thrown onto a level higher than the height of <Unit> or onto water, do not roll for throwing damage. The thrown figure does not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FERAL_RAGE', '', 'Feral Rage', '', 'When a <Unit> attacks, it may attack up to 2 additional times. A <Unit> cannot attack the same figure more than once per turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CHAIN_GRAB', '', 'Chain Grab', '', 'After moving and before attacking with a <Unit>, you may choose one opponent?s small or medium figure within three clear sight spaces whose base is no more than 6 levels above that <Unit>?s height or 6 levels below that <Unit>?s base. Roll the 20-sided die. If you roll a 9 or higher, place the chosen figure on any empty same-level space adjacent to that <Unit>. If the chosen figure is engaged when it is moved by Chain Grab, it will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LIFE_DRAIN', '', 'Life Drain', '', 'Each time <Unit> destroys a figure, you may remove a wound marker from this Army Card. <Unit> cannot Life Drain destructible objects.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MYSTIC_SACRIFICE', '', 'Mystic Sacrifice', '', 'After rolling the 20-sided die for the Army Card of a Small, Medium, or Large Unique Hero you control within 4 clear sight spaces of <Unit>, you may place 1 or 2 wound markers on <Unit>''s Army Card. Add 1 to your 20-sided die roll for each wound marker you placed, even if this was enough wounds to destroy <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('RECKLESS_SECOND_SWING', '', 'Reckless Second Swing', '', 'When <Unit> wounds a figure with his normal attack, he must attack one additional time, if possible.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MASTER_MANIPULATOR', '', 'Master Manipulator', '', 'After revealing an Order Marker on <Unit>''s card, instead of taking that turn with <Unit>, you may take a turn with any Unique Hero you control within clear sight of <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORC_ARCHER_ENHANCEMENT', '', 'Orc Archer Enhancement', '', 'All friendly Orc Archers adjacent to <Unit> receive an additional attack die and an additional defense die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORC_BATTLE_RUSH', '', 'Orc Battle Rush', '', 'At the start of the first round of the game, after all order markers have been placed, you may move all <Unit>s you control up to 5 spaces each. They must end their Orc Battle Rush movement on an empty space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STEALTH_LEAP', '', 'Stealth Leap', '', 'Instead of their normal move, any or all <Unit>s may Stealth Leap. Stealth Leap has a move of 3. When counting spaces for a <Unit>?s leaping movement, ignore elevations. A <Unit> may leap over water without stopping, pass over figures without becoming engaged, and leap over obstacles such as ruins. A <Unit> may not leap more than 12 levels up or down in a single leap. If a <Unit> is engaged when he starts to leap, he does not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SPEAR_OF_SUMMONING', '', 'Spear Of Summoning', '', 'After moving and before attacking, choose any figure you control on the battlefield, then roll a 20-sided die.  If you roll a 1-8, nothing happens.  If you roll a 9-20, move the chosen figure to any space adjacent to this creature.  If the summoned figure is engaged, the figure does not recieve any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SCURRY', '', 'Scurry', '', 'When a <Unit> you control is destroyed by a normal attack from an opponent?s figure, you may move any 2 <Unit>s you control up to 4 spaces each, and all <Unit>s you control no longer have any visible Hit Zones for the duration of the attacking figure?s turn while they remain unengaged. <Unit>s will never take any leaving engagement attacks while using Scurry.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MARKED_FOR_DESTRUCTION', '', 'Marked for Destruction', '', 'When a Soulborg figure that you control who follows Utgar attacks a figure engaged with <Unit> with a normal attack, before defense dice are rolled, you may re-roll one attack die that did not show a skull. Marked for Destruction can only be used once for each attack. Marked for Destruction does not affect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SEARING_INTENSITY', '', 'Searing Intensity', '', 'After moving and before attacking, you must roll the 20-sided die once for each figure adjacent to at least one <Unit> you control. If you roll a 14 or higher, that figure receives 1 wound. Figures with the Lava Resistant special power are not affected by Searing Intensity.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('A_COWARD_S_REWARD', '', 'A Coward''s Reward', '', '<Unit> rolls one additional die against figures leaving an engagement with him. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLEDGLING_FIRES_SPECIAL_ATTACK', '', 'Fledgling Fires Special Attack', '', 'Range 5. Attack 3.  Common Squad figures roll 2 fewer defense dice against Fledgling Fires Special Attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('IMPALE', '', 'Impale', '', 'When attacking a non-adjacent figure, a <Unit> receives an additional attack die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STEALTH_ARMOR_15', '', 'Stealth Armor 15', '', 'When a <Unit> receives one or more wounds, before removing that <Unit>, roll the 20-sided die. If you roll a 15 or higher, ignore any wounds. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ARCANE_RIPOSTE', '', 'Arcane Riposte', '', 'When <Unit> rolls defense dice against a normal attack, all excess shields count as unblockable hits on the attacking figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BLEND_INTO_SHADOW', '', 'Blend Into Shadow', '', '<Unit> starts each game with up to 2 shadow tiles on her Army Card. If <Unit> ends her turn on an empty land space, you may place a shadow tile from her Army Card onto the space she occupies if the shadow tile fits normally onto that space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLUTTER', '', 'Flutter', '', 'If <Unit> is attacked by an adjacent opponent''s figure and at least 1 skull is rolled, roll the 20-sided die.  If you roll 1-9, roll defense dice normally.  If you roll a 10 or higher, <Unit> takes no damage and may immediately move up to 2 spaces. If <Unit> is engaged when she starts to flutter, she will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ADJACENT_TOUGH_1', '', 'Adjacent Tough 1', '', 'When rolling defense dice for <Unit>, if <Unit> is adjacent to at least one figure you control who follows <General>, add one automatic shield to the defense roll.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SUPER_LEAP', '', 'Super Leap', '', 'Instead of his normal move, <Unit> may Super Leap. Super Leap has a move of 10. When counting spaces for <Unit>''s Super Leap movement, ignore elevations. <Unit> may leap over water without stopping, leap over figures without becoming engaged, and leap over obstacles such as ruins. <Unit> may not leap more than 50 levels up or down in a single leap. If <Unit> is engaged when he starts to leap, he will take any leaving engagement attacks. <Unit> rolls 3 less attack dice on any turn that he chooses to Super Leap.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DISMISS_THE_RABBLE', '', 'Dismiss The Rabble', '', 'When rolling defense dice against adjacent attacking small or medium Squad figures, <Unit>s receive 1 additional defense die.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BAYONET_ATTACK_1', '', 'Bayonet Attack 1', '', 'When rolling attack dice against an adjacent figure, a Soldier in the <Unit> adds 1 to his attack dice. A Soldier in the <Unit> can only use Bayonet Attack if he moved at least one space this turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DUAL_STRIKE', '', 'Dual Strike', '', 'When a <Unit> attacks, he may attack one additional time. A <Unit> cannot attack the same figure more than once per turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DYING_SWIPE', '', 'Dying Swipe', '', 'If this <Unit> is attacked with a normal attack by an opponent?s figure and receives enough wounds to be destroyed, roll 3 attack dice. One at a time for each skull rolled, you may inflict 1 wound on any figure that is adjacent to this <Unit>. Figures affected by Dying Swipe cannot roll any defense dice and can receive more than 1 wound each. After using Dying Swipe, remove this <Unit> from the battlefield.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CYBERCLAW', '', 'Cyberclaw', '', 'All small or medium opponent?s figures that enter or occupy a space adjacent to any <Unit> may not move. Figures affected by the Cyberclaw cannot be moved by any special power on an Army Card or Glyph.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('COSMIC_FORCE_BLAST_SPECIAL_ATTACK', '', 'Cosmic Force Blast Special Attack', '', 'Range 6. Attack 6. If an opponent''s Unique Hero receives at least one wound from the Cosmic Force Blast Special Attack, roll the 20-sided die. If you roll a 16 or higher, you may remove one unrevealed Order Marker at random from that Unique Hero''s Army Card.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('NEGATIVE_ELEMENT', '', 'Negative Element', '', 'A <Unit> can never roll defense dice while it is on a water space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BEAST_BONDING', '', 'Beast Bonding', '', 'Before taking a turn with this creature, you can take a turn with any Beast you control.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DIRECTED_FIRE', '', 'Directed Fire', '', 'Instead of attacking with <Unit> this turn, you may choose up to 3 unengaged Soulborg Squad figures you control who follow Jandar. Take a single turn with the chosen figures, during which the chosen figures may only attack. <Unit> must also have line of sight to any figure attacked using Directed Fire.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STEALTH_LEAP_25', '', 'Stealth Leap 25', '', 'Instead of his normal move, <Unit> may use Stealth Leap 25. Stealth Leap 25 has a move of 3. When counting spaces for Stealth Leap 25, ignore elevations. <Unit> may leap over water without stopping, leap over figures without becoming engaged, and leap over obstacles such as ruins. <Unit> may not leap more than 25 levels up or down in a single leap. If <Unit> is engaged when he starts his Stealth Leap 25, he will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('DUST_OF_DEATH', '', 'Dust Of Death', '', '"After moving and before attacking, you may either:<br/>*Roll the 20-sided die once for each figure adjacent to <Unit>, or<br/>*Roll the 20-sided die once for one figure up to 3 clear sight spaces away.<br/>If you roll 19 or higher, that figure is destroyed."', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LURK_IN_SHADOWS', '', 'Lurk in Shadows', '', 'If <Unit> is on a shadow space, opponents? figures must be adjacent to attack him with a normal attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ORC_BATTLE_CRY_AURA', '', 'Orc Battle Cry Aura', '', 'When attacking with any Orc Warrior figures you control within 2 clear sight spaces of <Unit>, you may roll Utgar Valkyrie dice. Each Utgar symbol rolled counts for an additional skull.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PHANTOM_WALK', '', 'Phantom Walk', '', '<Unit> can move through all figures and is never attacked when leaving an engagement. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FORCE_ORB_SPECIAL_ATTACK', '', 'Force Orb Special Attack', '', 'Range 5. Attack 3.  Choose an opponent?s figure to attack. Each opponent?s figure adjacent to the chosen figure is also affected by the Force Orb Special Attack. Roll attack dice once for all affected figures. Each figure rolls defense dice separately.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TOXIC_SKIN', '', 'Toxic Skin', '', 'After attacking, you must roll the 20-sided die once for each figure adjacent to <Unit>. If you roll a 17 or higher, that figure receives one wound. Soulborgs are not affected by Toxic Skin.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('A_BROTHER_S_CALL', '', 'A Brother''s Call', '', '<Unit> may add 1 to his Move number for each engaged Human you control who follows Jandar other than <Unit>, for a maximum of +3 for A Brother''s Call.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WOUNDED_SMASH', '', 'Wounded Smash', '', 'When <Unit> attacks, he receives an extra attack die for each wound marker he has. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SCALE_#', '', 'Scale #', '', 'When moving up or down levels of terrain, <Unit>s may add # to their height.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLEDGLING_ACID_BREATH', '', 'Fledgling Acid Breath', '', 'Instead of attacking with a <Unit>, you may choose one small or medium figure within 4 clear sight spaces of it. Roll the 20-sided die. If you roll a 1-10, nothing happens. If you roll an 11-15, the chosen figure receives 1 wound. If you roll a 16 or higher, the chosen figure receives 2 wounds.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STOMP_SPECIAL_ATTACK', '', 'Stomp Special Attack', '', 'Range 1. Attack 3.  Any figure adjacent to <Unit> is affected by the Stomp Special Attack. Roll 3 attack dice once for all affected figures. Each figure rolls defense separately. <Unit> cannot attack using his Stomp Special Attack on the same turn he uses Super Leap.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LIGHTNING_BREATH_SPECIAL_ATTACK', '', 'Lightning Breath Special Attack', '', '"Range 4+ Special. Attack 4.<br/>Choose a figure to attack. You may also chose a second figure within 3 clear sight spaces of the targeted figure and a third figure within 2 clear sight spaces of the second figure to be affected by Lightning Breath Special Attack. Roll attack dice once for all figures. Each figure rolls defense dice seperately. Lightning Breath Special Attack does not affect destructable objects."', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('ICE_TROLL_CHARGE', '', 'Ice Troll Charge', '', 'After moving and before attacking with this <Unit>, roll the 20-sided die. If this <Unit> is on a snow or ice space, add 3 to your die roll. If you roll a 13 or higher, you may move this <Unit> again.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SABER_STORM_SPECIAL_ATTACK', '', 'Saber Storm Special Attack', '', 'Range 1. Attack 1, 2 or 3.  <Unit> starts each turn with 6 attack dice. Choose any adjacent figure and attack by rolling 1, 2 or 3 attack dice. <Unit> may keep making special attacks with 1, 2 or 3 attack dice until she has rolled all 6 attack dice. <Unit> may target the same or different figures with each attack.', 1);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('SOLDIER_LEADERSHIP', '', 'Soldier Leadership', '', 'All Soldiers you control move 1 additional space.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FRENZY', '', 'Frenzy', '', 'After you take a turn with <Unit>, roll the 20-sided die. If you roll a 16 or higher you may take another turn with <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EXOSKELETON', '', 'Exoskeleton', '', 'At the start of the game, place 1 copper Exoskeleton marker on a <Unit> Army Card for each <Unit> figure in your Army. After a <Unit> you control rolls defense dice against a normal attack, you may remove 1 Exoskeleton marker from its Army Card to ignore all wounds inflicted by that attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('OMNICRON_MOBILIZATION', '', 'Omnicron Mobilization', '', 'Instead of moving with <Unit>, you may move up to 3 other Soulborg figures you control who follow Jandar up to 5 spaces each.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TASTE_OF_FREEDOM', '', 'Taste Of Freedom', '', 'If all of your Order Markers are on Gladiators, and at least one Order Marker is on <Unit>, all friendly Gladiators adjacent to <Unit> add 1 die to thier normal attack.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TACTICAL_SWITCH', '', 'Tactical Switch', '', 'When a <Unit> you control attacks an opponent?s small or medium figure, before rolling the attack dice, you may switch that <Unit> with the defending figure. After switching spaces, the <Unit> must continue that attack, if possible, and cannot attack any other figure this turn. Figures moved by Tactical Switch never take any leaving engagement attacks. An opponent?s figure may be moved by Tactical Switch only once per turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('LOLTH_S_JUDGEMENT_AURA', '', 'Lolth''s Judgement Aura', '', 'All Arachnid figures you control within 6 clear sight spaces of <Unit> roll 1 additional attack die when attacking normally.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('MOON_FRENZY', '', 'Moon Frenzy', '', 'After revealing an order marker on this <Unit>, before taking this <Unit>?s turn, roll the 20-sided die. If you roll an 11 or higher, you may first take a turn with any Hybrid Hero in play. If you take a turn with an opponent?s Hybrid Hero, you control that hero for the duration of its turn. At the end of its turn, control of the Hybrid Hero returns to its previous owner. All order markers that were on the figure?s Army Card will stay on the Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('STAFF_OF_LERKINTIN', '', 'Staff Of Lerkintin', '', 'When defending awith Arkmer, add as many defense dice as the number of Elves you control adjacent to Arkmer.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TARGETING_BEACON', '', 'Targeting Beacon', '', 'When attacking a non-adjacent figure, all Soulborg figures you control who follow Jandar add 1 die to their attack if at least one <Unit> you control is adjacent to the defending figure.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('WARFORGED_RESOLVE', '', 'Warforged Resolve', '', 'When rolling defense dice against normal or special attack, <Unit> always adds one automatic shield to whatever is rolled.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FIRST_STRIKE', '', 'First Strike', '', 'When Order Marker 1 is revealed on the <Unit>, add 2 to their Move number and 1 extra attack die for that turn.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TELEPORTATION', '', 'Teleportation', '', 'Instead of moving <Unit> normally, you may choose any empty space that is on the same level and within 10 spaces of <Unit>. Place <Unit> on the chosen space. When <Unit> starts to Teleport, if he is engaged he will not take any leaving engagement attacks.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('EXTENDED_DEFENSIVE_AURA', '', 'Extended Defensive Aura', '', 'All figures you control within 6 clear sight spaces of <Unit> add 1 to their defense dice. <Unit>?s Extended Defensive Aura does not affect <Unit>.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('PURPLE_HEART', '', 'Purple Heart', '', 'When a Human you control who follows Jandar and is adjacent to <Unit> receives one or more wounds from an attacking figure who is not adjacent, you may instead place all of those wound markers on this Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('TACTICIAN', '', 'Tactician', '', 'All friendly figures adjacent to <Unit> add 1 extra die to their attack, and 1 extra die to their defense.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CONCEALMENT_#', '', 'Concealment #', '', 'If a <Unit> you control is targeted and receives one or more wounds from an attacking figure who is not adjacent, you must roll the 20-sided die. Count the minimum number of spaces between the attacker and the <Unit>. Add this number to your die roll. If you roll ## or higher, ignore any wounds the <Unit> just received.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('BERSERKER_CHARGE_ENHANCEMENT', '', 'Berserker Charge Enhancement', '', 'Add 1 to your die roll when you roll for the Berserker Charge power on any Army Card.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('COMBAT_LEADER', '', 'Combat Leader', '', 'If at least one order marker is on <Unit>, you may add 3 to your initiative roll.', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FLYING', '', 'Flying', '', 'When counting spaces for <Unit>?s movement, ignore elevations. <Unit> may fly over water without stopping, pass over figures without becoming engaged, and fly over obstacles such as ruins. When <Unit> starts to fly, if it is engaged he will take any leaving engagement attacks.', 0);
