INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('HORDE_SHRIEK', '', 'Horde Shriek', '', 'After revealing an order marker and before taking a turn with this <Unit>, if this <Unit> is not engaged, you may first take a turn with another Undead Savage Hero or Squad that you control.', 0);
