INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('FIRST_ASSAULT_3', '', 'First Assault 3', '', 'When attacking with <Unit>, if the defending figure was not adjacent to <Unit> at the start of this turn, <Unit> receives 3 additional attack dice. ', 0);
INSERT INTO `powers` (`id`, `name_fr`, `name_en`, `text_fr`, `text_en`, `is_special_attack`) VALUES ('CIRCUITRY_OVERLOAD', '', 'Circuitry Overload', '', 'After moving and before attacking, you must roll the 20-sided die once for each Soulborg figure adjacent to any <Unit>s you control. If the figure is a Squad figure and you roll a 13 or higher, destroy that figure. If the figure is a Hero figure and you roll a 16 or higher, that figure receives a wound. <Unit>s are not affected by Circuitry Overload.', 0);
